// APIClient manages communication with the EHR API API vlatest
// In most cases there should be only one, shared, APIClient.
type APIClient struct {
	cfg    *Configuration
	common service // Reuse a single struct instead of allocating one for each service on the heap.

	// API Services

	COMPOSITIONApi *COMPOSITIONApiService

	CONTRIBUTIONApi *CONTRIBUTIONApiService

	DIRECTORYApi *DIRECTORYApiService

	EHRApi *EHRApiService

	EHRSTATUSApi *EHRSTATUSApiService
}

type service struct {
	client *APIClient
}

// NewAPIClient creates a new API client. Requires a userAgent string describing your application.
// optionally a custom http.Client to allow for advanced features such as caching.
func NewAPIClient(cfg *Configuration) *APIClient {
	if cfg.HTTPClient == nil {
		cfg.HTTPClient = http.DefaultClient
	}

	c := &APIClient{}
	c.cfg = cfg
	c.common.client = c

	// API Services
	c.COMPOSITIONApi = (*COMPOSITIONApiService)(&c.common)
	c.CONTRIBUTIONApi = (*CONTRIBUTIONApiService)(&c.common)
	c.DIRECTORYApi = (*DIRECTORYApiService)(&c.common)
	c.EHRApi = (*EHRApiService)(&c.common)
	c.EHRSTATUSApi = (*EHRSTATUSApiService)(&c.common)

	return c
}

